let assert = require('assert');
let wordwrap = require('wordwrap');

let fs = require('fs');
let idleness = fs.readFileSync(__dirname + '/idleness.txt', 'utf8');

exports.stop80 = function () {
    let lines = wordwrap(80)(idleness).split(/\n/);
    let words = idleness.split(/\s+/);
    
    lines.forEach(function (line) {
        assert.ok(line.length <= 80, 'line > 80 columns');
        let chunks = line.match(/\S/) ? line.split(/\s+/) : [];
        assert.deepEqual(chunks, words.splice(0, chunks.length));
    });
};

exports.start20stop60 = function () {
    let lines = wordwrap(20, 100)(idleness).split(/\n/);
    let words = idleness.split(/\s+/);
    
    lines.forEach(function (line) {
        assert.ok(line.length <= 100, 'line > 100 columns');
        let chunks = line
            .split(/\s+/)
            .filter(function (x) { return x.match(/\S/) })
        ;
        assert.deepEqual(chunks, words.splice(0, chunks.length));
        assert.deepEqual(line.slice(0, 20), new Array(20 + 1).join(' '));
    });
};
